// Функция для отображения списка аниме
function displayAnimeList(animeList) {
    const animeListElement = document.getElementById("anime-list");

    animeList.forEach(anime => {
        const listItem = document.createElement("li");
        const link = document.createElement("a");
        const image = document.createElement("img");
        const title = document.createElement("h3");
        const description = document.createElement("p");
        const authors = document.createElement("p");
        const genres = document.createElement("p");

        link.href = "#"; // Здесь нужно добавить ссылку на страницу с деталями аниме
        image.src = anime.imageUrl;
        title.textContent = anime.title;
        description.textContent = anime.description;
        authors.textContent = "Автори: " + anime.authors.join(", "); // Объединяем имена авторов через запятую
        genres.textContent = "Жанри: " + anime.genres.join(", "); // Объединяем жанры через запятую

        link.appendChild(image);
        link.appendChild(title);
        link.appendChild(description);
        link.appendChild(authors);
        link.appendChild(genres);

        const detailLink = document.createElement("a"); // Добавляем ссылку для подробного описания аниме
        detailLink.textContent = "Більше"; // Текст ссылки
        detailLink.href = anime.filePath; // Ссылка на страницу с подробным описанием аниме
        detailLink.classList.add("detail-link"); // Добавляем класс для стилизации ссылки, если необходимо

        listItem.appendChild(link);
        listItem.appendChild(detailLink); // Добавляем ссылку после блока с описанием аниме

        animeListElement.appendChild(listItem);
    });
}

// Функция для обновления списка аниме в соответствии с запросом поиска
function updateAnimeList(query) {
    const filteredAnimeList = animeList.filter(anime =>
        anime.title.toLowerCase().includes(query.toLowerCase()) || // Поиск по названию
        (anime.authors && anime.authors.join(" ").toLowerCase().includes(query.toLowerCase())) || // Поиск по авторам
        (anime.genres && anime.genres.join(" ").toLowerCase().includes(query.toLowerCase())) // Поиск по жанрам
    );

    const animeListElement = document.getElementById("anime-list");
    animeListElement.innerHTML = ""; // Очищаем список перед добавлением отфильтрованных результатов

    if (filteredAnimeList.length === 0) {
        animeListElement.innerHTML = "<li>Нічого не знайдено.</li>";
    } else {
        displayAnimeList(filteredAnimeList);
    }
}
// Обработчик события для строки поиска
document.getElementById("search-input").addEventListener("input", function(event) {
    const query = event.target.value.trim(); // Получаем текст из строки поиска и удаляем пробелы по краям
    updateAnimeList(query);
});
// Изначально отображаем список аниме
displayAnimeList(animeList);
